import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { toggleModal } from '../../store/redux_action';
import { AppState } from '../../store/redux_store';

const ModalComponent: React.FC = () => {
  const dispatch = useDispatch();
  const { isOpen, content } = useSelector((state: AppState) => state.modal);

  console.log('Modal isOpen:', isOpen);
  console.log('Modal content:', content);

  const handleCloseModal = () => {
    dispatch(toggleModal(false)); // モーダルを閉じる
  };

  return (
    <>
      {isOpen && (
        <div className="modal-overlay" onClick={handleCloseModal} style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center'
        }}>
          <div className="modal-content" style={{
            backgroundColor: 'white',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center',
            maxWidth: '80%',
            maxHeight: '80%',
            overflowY: 'auto'
          }}>
            <h2>モーダルタイトル</h2>
            <div>{content}</div>
            <button onClick={handleCloseModal}>閉じる</button>
          </div>
        </div>
      )}
    </>
  );
};

export default ModalComponent;
