// src/pages/body-index/body-index.tsx
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { AppState } from '../../store/redux_store';

const BodyIndex: React.FC = () => {
  const dispatch = useDispatch();
  const { height, weight, bmi } = useSelector((state: AppState) => state.bmi);

  const handleHeightChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch({ type: 'SET_HEIGHT', payload: Number(e.target.value) });
  };

  const handleWeightChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch({ type: 'SET_WEIGHT', payload: Number(e.target.value) });
  };

  const handleCalculateBMI = () => {
    dispatch({ type: 'CALCULATE_BMI' });
  };

  return (
    <div>
      <h1>BMI計算ページ</h1>
      <div>
        <label htmlFor="height">身長 (cm):</label>
        <input
          id="height"
          type="number"
          value={height}
          onChange={handleHeightChange}
        />
      </div>
      <div>
        <label htmlFor="weight">体重 (kg):</label>
        <input
          id="weight"
          type="number"
          value={weight}
          onChange={handleWeightChange}
        />
      </div>
      <button onClick={handleCalculateBMI}>計算</button>
      {bmi !== null && (
        <div>
          <h2>BMI: {bmi.toFixed(2)}</h2>
        </div>
      )}
    </div>
  );
};

export default BodyIndex;
