import { Bar, Line, Radar } from 'react-chartjs-2';
import MainLayout from '../mainlayout';
import { useDispatch, useSelector } from 'react-redux';
import { AppState } from '../../store/redux_store';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  RadialLinearScale,  
  BarElement,
  PointElement,  
  LineElement,   
  Title,
  Tooltip,
  Legend,
  Filler          
} from 'chart.js';

ChartJS.register(CategoryScale,LinearScale,RadialLinearScale, BarElement,PointElement,  LineElement,  Title,Tooltip,Legend,Filler);


const Dashboard: React.FC = () => {
  const dispatch = useDispatch();
  const nutrition = useSelector((state: AppState) => state.food);
  console.log('nutrition', nutrition)
  const calories = nutrition.map(item => item.calories);
  console.log('calories',calories)

  const data = {
    labels: [
      'Eating',
      'Drinking',
      'Sleeping',
      'Designing',
      'Coding',
      'Cycling',
      'Running'
    ],
    datasets: [{
      label: 'My First Dataset',
      data: calories,
      fill: true,
      backgroundColor: 'rgba(255, 99, 132, 0.2)',
      borderColor: 'rgb(255, 99, 132)',
      pointBackgroundColor: 'rgb(255, 99, 132)',
      pointBorderColor: '#fff',
      pointHoverBackgroundColor: '#fff',
      pointHoverBorderColor: 'rgb(255, 99, 132)'
    }, {
      label: 'My Second Dataset',
      data: [28, 48, 40, 19, 96, 27, 100],
      fill: true,
      backgroundColor: 'rgba(54, 162, 235, 0.2)',
      borderColor: 'rgb(54, 162, 235)',
      pointBackgroundColor: 'rgb(54, 162, 235)',
      pointBorderColor: '#fff',
      pointHoverBackgroundColor: '#fff',
      pointHoverBorderColor: 'rgb(54, 162, 235)'
    }]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: 'Chart.js Bar Chart in React',
      },
    },
  };

  return (
    <MainLayout>
    <div>
      <h1>dashboard Page</h1>
      <p>Welcome to the dashboard.</p>
      <div className='date-div'>
            <label className='lavel'>日付：</label>
                <input id='date' className='select-kind' type="date" />
      </div>
      <div className='radar-chart'>
        <Radar data={data} options={options} />;
      </div>
      <div className='line-chart'>
        <Line data={data} options={options} />;
      </div>
      <div className='bar-chart'>
        <Bar data={data} options={options} />;
      </div>
    </div>
    </MainLayout>
  );
};

export default Dashboard;
