import React, { useEffect,useState,useMemo } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { format } from "date-fns";
import { useDispatch, useSelector } from 'react-redux';
import { AppState } from '../../store/redux_store';
import { Radar, Line, Bar } from 'react-chartjs-2';
import MainLayout from '../mainlayout';
import './dashboard.css'
import { setDateLine, setDateRadar , setNutLine, setNutRadar, setWeekLine} from '../../store/redux_action';
import {store} from '../../store/redux_store';
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  CategoryScale,
  LineElement,
  BarElement,
  LinearScale,
  Title,
  Tooltip,
  Legend,
  Filler,
  TooltipItem, 
  TooltipModel 
} from 'chart.js';

ChartJS.register(
  RadialLinearScale,
  PointElement,
  LineElement,
  BarElement,
  CategoryScale,
  LinearScale, 
  Title,
  Tooltip,
  Legend,
  Filler
);

const Barchart = () => {
    const DATA_COUNT = 7;
    const NUMBER_CFG = {count: DATA_COUNT, min: -100, max: 100};

    const labels = ["1","2","3","4","5","6","7",];
    const data = {
    labels: labels,
    datasets: [
        {
        label: 'Dataset 1',
        data: labels.map(() => {
            return [40,50];
        }),
        backgroundColor: 'rgb(255, 99, 132)',
        },
        {
        label: 'Dataset 2',
        data: labels.map(() => {
            return [60,70];
        }),
        backgroundColor:'rgb(255, 99, 132)',
        },
    ]
    };

      const options = {
        responsive: true,
        plugins: {
          legend: {
            position: 'top' as const,
          },
          title: {
            display: true,
            text: 'カロリー摂取量の統計',
          },
        },
        scales: {
          x: {
            stacked: true,
            categoryPercentage: 0.6, // 棒グラフの幅
            barPercentage: 0.8, // 棒グラフの間隔
          },
          y: {
            beginAtZero: true,
          },
        },
      };
    return (
        <div>
            <Bar data={data} options={options}/>
        </div>
    );
};

export default Barchart;
