import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import MainLayout from '../mainlayout';
import { AppState } from '../../store/redux_store';
import { setSelectedFoodCategory } from '../../store/redux_action';

type FoodItem = {
    id: number;
    date: string;
    foodType: string;
    amount: number;
    calories: number;
  };

const CalorieCalculation: React.FC = () => {
    const dispatch = useDispatch();
    const [foodItems, setFoodItems] = useState<FoodItem[]>([]);
    const [selectedFood, setSelectedFood] = useState('');
    const [date, setDate] = useState<string>(new Date().toISOString().slice(0, 10));
    const [amount, setAmount] = useState<number>(1);
    const [calories, setCalories] = useState<number>(0);
    const { selectedCategory, foodData } = useSelector((state: AppState) => state.selectfoodtype);

    const handleTypeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        dispatch(setSelectedFoodCategory(e.target.value));
      };
    const filteredFood = foodData.filter(food => food.type === selectedCategory);

    const handleAddToTable = () => {
        const selectedFoodItem = filteredFood.find(food => food.id === parseInt(selectedFood));
        if (!selectedFoodItem) return;

        setFoodItems([
          ...foodItems,
          {
            id: Date.now(),
            date: date,
            foodType: selectedFoodItem.title,
            amount: amount,
            calories: selectedFoodItem.calorie * amount,
          },
        ]);
      };

      const handledeleteToTable = (id:number) => {
        const updatedFoodItems = foodItems.filter(item => item.id !== id);
        setFoodItems(updatedFoodItems);
      }

  return (
    <MainLayout>
    <div className="calorie-tracker-container">
        <h2>カロリー計算</h2>
        <div>
      <div className='option-calorie'>
        <label>種類を選ぶ</label>
        <select value={selectedCategory || ''} onChange={handleTypeChange}>
          <option value="meat">Meat</option>
          <option value="egg">Egg</option>
          <option value="fish">Fish</option>
          <option value="milk and cheese">Milk and Cheese</option>
          <option value="vegetable">Vegetable</option>
        </select>
      </div>

      <table className="food-table">
        <thead>
          <tr>
            <th>Food</th>
            <th>Calories</th>
            <th>Protein</th>
            <th>Fat</th>
          </tr>
        </thead>
        <tbody>
          {filteredFood.map(food => (
            <tr key={food.id}>
              <td>{food.title}</td>
              <td>{food.calorie} cal</td>
              <td>{food.protein} g</td>
              <td>{food.fat} g</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>

      <div className="form-section">
        <div>
          <label>日付</label>
          <input 
            type="date" 
            value={date} 
            onChange={e => setDate(e.target.value)} 
          />
        </div>
        <div>
          <label>食品</label>
          <select 
            value={selectedFood} 
            onChange={e => setSelectedFood(e.target.value)}
          >
            {filteredFood.map(food => (
              <option key={food.id} value={food.id}>{food.title}</option>
            ))}
          </select>
        </div>
        <div>
          <label>量</label>
          <input 
            type="number" 
            value={amount} 
            onChange={e => setAmount(Number(e.target.value))}
          />
        </div>
        <button onClick={handleAddToTable}>テーブルに追加</button>
      </div>

      <table className="calorie-table">
        <thead>
          <tr>
            <th>日付</th>
            <th>食品</th>
            <th>量</th>
            <th>カロリー</th>
            <th>アクション</th>
          </tr>
        </thead>
        <tbody>
          {foodItems.map((item) => (
            <tr key={item.id}>
              <td>{item.date}</td>
              <td>{item.foodType}</td>
              <td>{item.amount}</td>
              <td>{item.calories}</td>
              <td>
                <button>編集</button>
                <button onClick={() => handledeleteToTable(item.id)}>削除</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </MainLayout>
  );
};

export default CalorieCalculation;
