import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { AppState } from '../../store/redux_store';
import { Bar, Line, Radar } from 'react-chartjs-2';
import MainLayout from '../mainlayout';
import { updateNutrition } from '../../store/redux_action';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  RadialLinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  RadialLinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

interface ChartData {
  labels: string[];
  datasets: {
    label: string;
    data: number[];
    fill: boolean;
    backgroundColor: string;
    borderColor: string;
    pointBackgroundColor: string;
    pointBorderColor: string;
    pointHoverBackgroundColor: string;
    pointHoverBorderColor: string;
  }[];
}

const Dashboard: React.FC = () => {
  const dispatch = useDispatch();
  const [data, setData] = useState<ChartData>({
    labels: [],
    datasets: [
      {
        label: 'Calories',
        data: [],
        fill: true,
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderColor: 'rgb(255, 99, 132)',
        pointBackgroundColor: 'rgb(255, 99, 132)',
        pointBorderColor: '#fff',
        pointHoverBackgroundColor: '#fff',
        pointHoverBorderColor: 'rgb(255, 99, 132)'
      }
    ]
  });

  const nutritionItems = useSelector((state: AppState) => state.food);
  console.log('food', nutritionItems);

  useEffect(() => {
    const savedNutritionItems = localStorage.getItem('nutritionItems');
    if (savedNutritionItems) {
      const parsedItems = JSON.parse(savedNutritionItems);
      dispatch(updateNutrition(parsedItems));

      // 個々のカロリーを表示するためのデータ準備
      const labels = parsedItems.map((item: any) => item.date); // 日付や食事のラベル
      const caloriesData = parsedItems.map((item: any) => item.calories/100); // 各食事のカロリー

      setData({
        labels,
        datasets: [
          {
            label: 'Calories',
            data: [],
            fill: true,
            backgroundColor: 'rgba(255, 99, 132, 0.2)',
            borderColor: 'rgb(255, 99, 132)',
            pointBackgroundColor: 'rgb(255, 99, 132)',
            pointBorderColor: '#fff',
            pointHoverBackgroundColor: '#fff',
            pointHoverBorderColor: 'rgb(255, 99, 132)'
          }
        ]
      });
    }
  }, [dispatch]);

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const
      },
      title: {
        display: true,
        text: 'Nutrition Data Visualization'
      }
    }
  };

  return (
    <MainLayout>
      <div>
        <h1>Dashboard Page</h1>
        <p>Welcome to the dashboard.</p>
        <div className='date-div'>
          <label className='label'>日付：</label>
          <input id='date' className='select-kind' type="date" />
        </div>
        <div className='radar-chart'>
          <Radar data={data} options={options} />
        </div>
        <div className='line-chart'>
          <Line data={data} options={options} />
        </div>
        <div className='bar-chart'>
          <Bar data={data} options={options} />
        </div>
      </div>
    </MainLayout>
  );
};

export default Dashboard;
