import {initialUserState, UserState,initialPostState, Action, PostsState} from './redux_action';


export const postsReducer = (state = initialPostState, action: Action): PostsState => {
  switch (action.type) {
    case 'SET_POSTS':
      return { ...state, posts: action.payload };
    default:
      return state;
  }
};

export const toggleMenu = (menu: string) => ({
  type: 'TOGGLE_MENU',
  payload: menu,
});

export const userReducer = (state = initialUserState, action: Action): UserState => {
  switch (action.type) {
    case 'SET_USER_INFO':
      return { ...state, email: action.payload.email, password: action.payload.password };
    case 'SET_TOKEN':
      return { ...state, token: action.payload };
    default:
      return state;
  }
};
