import React, { useEffect,useState,useMemo } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { format } from "date-fns";
import { useDispatch, useSelector } from 'react-redux';
import { AppState } from '../../store/redux_store';
import { Radar, Line, Bar } from 'react-chartjs-2';
import MainLayout from '../mainlayout';
import './dashboard.css'
import { setDateLine, setDateRadar , setNutLine, setNutRadar, setWeekLine, setDateBar,setNutBar} from '../../store/redux_action';
import {store} from '../../store/redux_store';
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  CategoryScale,
  LineElement,
  BarElement,
  LinearScale,
  Title,
  Tooltip,
  Legend,
  Filler,
  TooltipItem, 
  TooltipModel 
} from 'chart.js';

ChartJS.register(
  RadialLinearScale,
  PointElement,
  LineElement,
  BarElement,
  CategoryScale,
  LinearScale, 
  Title,
  Tooltip,
  Legend,
  Filler
);

const Barchart = () => {
    const dispatch = useDispatch();
    const selectNut_bar = useSelector((state: AppState) => state.nut_bar);
    const selectDate_bar = useSelector((state: AppState) => state.date_bar);
    const initialDate = selectDate_bar.date_bar
    ? new Date(selectDate_bar.date_bar)
    : new Date(); // デフォルトの日付を設定

  // `DatePicker` 用の適切な初期値を設定
  const formattedDate = !isNaN(initialDate.getTime())
    ? initialDate
    : new Date(); // 無効な日付の場合はデフォルトの日付を設定
console.log(formattedDate)

    console.log("aa",initialDate)
    const data = {
        labels: ['January', 'February', 'March', 'April'],
        datasets: [
          {
            label: 'Product A',
            data: [75, 49, 70, 91],
            backgroundColor: 'rgba(255, 99, 132, 0.6)',
          },
          {
            label: 'Product B',
            data: [75, 49, 70, 91],
            backgroundColor: 'rgba(54, 162, 235, 0.6)',
          },
          {
            label: 'Product C',
            data: [55, 39, 60, 71],
            backgroundColor: 'rgba(75, 192, 192, 0.6)',
          },
          {
            label: 'Product D',
            data: [85, 69, 90, 101],
            backgroundColor: 'rgba(153, 102, 255, 0.6)',
          },
        ],
      };

      const options = {
        responsive: true,
        plugins: {
        legend: {
            position: 'top' as const,
        },
        title: {
            display: true,
            text: 'カロリー摂取量の統計',
        },
        },
        scales: {
        x: {
            stacked: false,
            categoryPercentage: 0.6, // 棒グラフの幅
            barPercentage: 0.8, // 棒グラフの間隔
        },
        y: {
            beginAtZero: true,
        },
        },
    };

    const handleMonthChange = (date: Date | null) => {
        if (date) {
          dispatch(setDateBar(format(date, "yyyy/MM/dd")));
        }
      };

    /*const handleMonthChange = (date: Date | null) => {
        if (date) {
          //dispatch(setDateBar(format(date, "yyyy/MM/dd")));
        }
      };*/
    return (
    <div>
        <DatePicker
        selected={formattedDate}
        onChange={handleMonthChange}
        dateFormat="yyyy/MM/dd"
        showMonthYearPicker
        showFullMonthYearPicker
        placeholderText="Select a month"
        />
        <div>
            <Bar data={data} options={options}/>
        </div>
    </div>
    );
};

export default Barchart;
