import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { AppState } from '../../store/redux_store';
import { Radar } from 'react-chartjs-2';
import MainLayout from '../mainlayout';
import { setDateRadar , setNutRadar} from '../../store/redux_action';
import {store} from '../../store/redux_store';
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';
import { nut_radarReducer } from '../../store/redux_reducer';

ChartJS.register(
  RadialLinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

interface ChartData {
  labels: string[];
  datasets: {
    label: string;
    data: number[];
    fill: boolean;
    backgroundColor: string;
    borderColor: string;
    pointBackgroundColor: string;
    pointBorderColor: string;
    pointHoverBackgroundColor: string;
    pointHoverBorderColor: string;
  }[];
}

const Dashboard: React.FC = () => {
  const dispatch = useDispatch();
  console.log('現在のReduxの状態:', store.getState());
  const nutritionItems = useSelector((state: AppState) => state.nutrition);
  const selectNut_radar = useSelector((state: AppState) => state.nut_radar);
  const selectDate_radar = useSelector((state: AppState) => state.date_radar);


  const [data, setData] = useState<ChartData>({
    labels: [],
    datasets: [
      {
        label: "",
        data: [],
        fill: true,
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderColor: 'rgb(255, 99, 132)',
        pointBackgroundColor: 'rgb(255, 99, 132)',
        pointBorderColor: '#fff',
        pointHoverBackgroundColor: '#fff',
        pointHoverBorderColor: 'rgb(255, 99, 132)'
      }
    ]
  });


  useEffect(() => {
      const labels = nutritionItems.map((item: any) => item.title); 
      const Data = nutritionItems.map((item: any) => item[selectNut_radar.nut_rader]/100); 
      setData({
        labels:labels,
        datasets: [
          {
            label: selectNut_radar.nut_rader.charAt(0).toUpperCase() + selectNut_radar.nut_rader.slice(1),
            data: Data,
            fill: true,
            backgroundColor: 'rgba(255, 99, 132, 0.2)',
            borderColor: 'rgb(255, 99, 132)',
            pointBackgroundColor: 'rgb(255, 99, 132)',
            pointBorderColor: '#fff',
            pointHoverBackgroundColor: '#fff',
            pointHoverBorderColor: 'rgb(255, 99, 132)'
          },
        ]
      });
  }, [selectNut_radar, nutritionItems]);

  const handleSelectChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    dispatch(setNutRadar(e.target.value));
  };

  const handleDateChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(setDateRadar(e.target.value));
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const
      },
      title: {
        display: true,
        text: 'Calories Visualization'
      }
    },
    scales: {
      r: {
        min: 0,  
        ticks: {
          stepSize: 100, 
        },
      },
    },

  };

  return (
    <MainLayout>
      <div>
        <h1>Dashboard Page</h1>
        <p>Welcome to the dashboard.</p>
        <div className='dash-date'>
          <label className='dash-date'>日付：</label>
          <input id='date' className='select-kind' type="date" value={selectDate_radar.date_rader || ''} onChange={handleDateChange}/>
        </div>
        <div className='dash-nutri'>
          <label className='dash-kind'>栄養：</label>
          <select id='date' className='select-kind' onChange={handleSelectChange} value={selectNut_radar.nut_rader}>
            <option value="">選択してください</option>
            <option value="calorie">calorie</option>
            <option value="protein">protein</option>
            <option value="fat">fat</option>
          </select>
        </div>
        <div className='radar-chart'>
          <Radar data={data} options={options} />
        </div>
      </div>
    </MainLayout>
  );
};

export default Dashboard;
