import React, { useEffect,useState,useMemo } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { format } from "date-fns";
import { useDispatch, useSelector } from 'react-redux';
import { AppState } from '../../store/redux_store';
import { Radar, Line } from 'react-chartjs-2';
import MainLayout from '../mainlayout';
import './dashboard.css'
import { setDateRadar , setNutRadar, setWeekLine} from '../../store/redux_action';
import {store} from '../../store/redux_store';
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
  TooltipItem, 
  TooltipModel 
} from 'chart.js';

ChartJS.register(
  RadialLinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

interface LineChartData {
  labels: string[];
    datasets: {
        label: string;
        data: number[];
        borderColor: string;
        backgroundColor: string;
        fill: boolean;
    }[];
  };

const Linechart = () => {
  const nutritionItems = useSelector((state: AppState) => state.nutrition);
  const selectNut_radar = useSelector((state: AppState) => state.nut_radar);
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);

  const getWeekRange = (date: Date): { startDate: Date; endDate: Date } => {
    const startOfWeek = date.getDate() - date.getDay();
    const startDate = new Date(date);
    startDate.setDate(startOfWeek);
    const endDate = new Date(date);
    endDate.setDate(startOfWeek + 6);
    return { startDate, endDate };
  };

  const handleDateChange = (date: Date | null) => {
    setSelectedDate(date);
  };

  const [lineData, setLineData] = useState<LineChartData>({
    labels: [],
    datasets: [
      {
        label: '',
        data: [],
        borderColor: '',
        backgroundColor: '',
        fill: false,
      },
    ],
  });

  const [options, setOptions] = useState({
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const
      },
      title: {
        display: true,
        text: '',
        font: {
          size: 24,
        },
      },
      tooltip: {
        callbacks: {
          label: function (context: TooltipItem<'radar'>) {
            return context.label + ': ' + context.raw; 
          },
        },
        bodyFont: {
          size: 14, 
        },
      },
    },
    scales: {
      x: {
        ticks: {
          font: {
            size: 14,
          },
        },
      },
      y: {
        ticks: {
          font: {
            size: 14,
          },
        },
      },
    },
  });

  useEffect(() => {
    const filteredItems = useMemo(() => {
      if (selectedDate) {
        const { startDate, endDate } = getWeekRange(selectedDate);
        return nutritionItems.filter(item => {
          const itemDate = new Date(item.date);
          return itemDate >= startDate && itemDate <= endDate;
        });
      }
      return [];
    }, [selectedDate, nutritionItems]);

    if(filteredItems.length > 0){
      const labels = filteredItems.map((item: any) => item.title); 
      let Data = filteredItems.map((item: any) => item[selectNut_radar.nut_rader]/100); 
      if(selectNut_radar.nut_rader === 'calories'){
        setRadarData({
          labels:labels,
          datasets: [
            {
              labels: [],
              datasets: [
                {
                  label: '',
                  data: [],
                  borderColor: '',
                  backgroundColor: '',
                  fill: false,
                },
              ],
            },
          ]
        });
      }else if(selectNut_radar.nut_rader === 'protein'){
        setRadarData({
          labels:labels,
          datasets: [
            {
              label: selectNut_radar.nut_rader.charAt(0).toUpperCase() + selectNut_radar.nut_rader.slice(1),
              data: Data,
              fill: true,
              backgroundColor: '#5a8dfb86',
              borderColor: '#2167ff',
              pointBackgroundColor: '#568bff',
              pointBorderColor: '#ffffff',
              pointHoverBackgroundColor: '#fff',
              pointHoverBorderColor: '#939393'
            },
          ]
        });
      }else if(selectNut_radar.nut_rader === 'fat'){
        setRadarData({
          labels:labels,
          datasets: [
            {
              label: selectNut_radar.nut_rader.charAt(0).toUpperCase() + selectNut_radar.nut_rader.slice(1),
              data: Data,
              fill: true,
              backgroundColor: '#8aff848f',
              borderColor: '#31b62a',
              pointBackgroundColor: '#31b62a',
              pointBorderColor: '#ffffff',
              pointHoverBackgroundColor: '#fff',
              pointHoverBorderColor: '#939393'
            },
          ]
        });
      }
    }else{
      setRadarData({
        labels: ["","","","","","",""],
        datasets: [
          {
            label: "Nodata",
            data: [0,0,0,0,0,0,0],
            fill: true,
            backgroundColor: 'rgba(255, 99, 132, 0.2)',
            borderColor: 'rgb(255, 99, 132)',
            pointBackgroundColor: 'rgb(142, 142, 142)',
            pointBorderColor: '#fff',
            pointHoverBackgroundColor: '#fff',
            pointHoverBorderColor: '#939393'
          },
        ]
      });
    };

    const stepSize = selectNut_radar.nut_rader === 'calories' ? 100 : 10;

    setOptions({
      ...options,
      plugins: {
        legend: {
          position: 'top' as const
        },
        title: {
          display: true,
          text: 'Radar-Chart',
          font: {
            size: 24, 
          },
        },
        tooltip: {
          callbacks: {
            label: function (context: TooltipItem<'radar'>) {
              return context.label + ': ' + context.raw;
            },
          },
          bodyFont: {
            size: 20, 
          },
        },
      },
      scales: {
        x: {
          ticks: {
            font: {
              size: 14,
            },
          },
        },
        y: {
          ticks: {
            font: {
              size: 14,
            },
          },
        },

      },


    });
  }, [selectDate_radar,selectNut_radar, nutritionItems]);


  return (
    <div>
      <DatePicker
        selected={selectedDate}
        onChange={handleDateChange}
        dateFormat="yyyy/MM/dd"
        showWeekNumbers
        filterDate={(date: Date) => date.getDay() === 0} 
        customInput={
          <input 
            type="text"
            value={
              selectedDate
                ? `第${Math.ceil((selectedDate.getDate() - 1) / 7) + 1}週目 (${format(selectedDate, "yyyy/MM/dd")})`
                : ""
            }
          />
        }
      />

    </div>
  );
};

export default Linechart;
