import { createStore, combineReducers } from 'redux';
import { persistStore, persistReducer } from 'redux-persist';
import storage from 'redux-persist/lib/storage';
import { userReducer, postsReducer, menuReducer, modalReducer, bmiReducer, bmrReducer, foodReducer, nutritionReducer } from './redux_reducer';
import { Action } from './redux_action';
import {UserState,PostsState,MenuState,ModalState,BMIState,BMRState,FoodState,NutritionState} from './redux_action'

// RootState 型を定義
export type RootState = {
  user: UserState;
  posts: PostsState;
  menu: MenuState;
  modal: ModalState;
  bmi: BMIState;
  bmr: BMRState;
  food: FoodState;
  nutrition: NutritionState;
};

// すべてのリデューサーをまとめた rootReducer を定義
const rootReducer = combineReducers({
  user: userReducer,
  posts: postsReducer,
  menu: menuReducer,
  modal: modalReducer,
  bmi: bmiReducer,
  bmr: bmrReducer,
  food: foodReducer,
  nutrition: nutritionReducer,
});

// Redux Persistの設定
const persistConfig = {
  key: 'root',
  storage,
};

// 型引数に RootState を指定して persistReducer を作成
const persistedReducer = persistReducer<RootState>(persistConfig, rootReducer);

// ストアの作成
export const store = createStore(persistedReducer);
export const persistor = persistStore(store);
