import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { AppState } from '../../store/redux_store';
import { Radar } from 'react-chartjs-2';
import MainLayout from '../mainlayout';
import { updateNutrition } from '../../store/redux_action';
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';

ChartJS.register(
  RadialLinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

interface ChartData {
  labels: string[];
  datasets: {
    label: string;
    data: number[];
    fill: boolean;
    backgroundColor: string;
    borderColor: string;
    pointBackgroundColor: string;
    pointBorderColor: string;
    pointHoverBackgroundColor: string;
    pointHoverBorderColor: string;
  }[];
}

const Dashboard: React.FC = () => {
  const dispatch = useDispatch();
  const [selectedDate, setSelectedDate] = useState<string>(''); // 選択された日付を保持する状態
  const nutritionItems = useSelector((state: AppState) => state.nutrition);
    console.log('nutrition', nutritionItems)
  const [data, setData] = useState<ChartData>({
    labels: [],
    datasets: [
      {
        label: 'Calories',
        data: [],
        fill: true,
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderColor: 'rgb(255, 99, 132)',
        pointBackgroundColor: 'rgb(255, 99, 132)',
        pointBorderColor: '#fff',
        pointHoverBackgroundColor: '#fff',
        pointHoverBorderColor: 'rgb(255, 99, 132)'
      }
    ]
  });


  useEffect(() => {
    const savedNutritionItems = localStorage.getItem('nutritionItems');

    if (savedNutritionItems) {
      const parsedItems = JSON.parse(savedNutritionItems);
      dispatch(updateNutrition(parsedItems));

      // 個々のカロリーを表示するためのデータ準備
      const filteredItems = parsedItems.filter((item: any) => item.title); // 食事のタイトルやラベル
      const caloriesData = parsedItems.map((item: any) => item.calories/100); // 各食事のカロリー

      setData({
        labels: filteredItems.map(item => item.title), // タイトルをラベルに設定
        datasets: [
          {
            label: 'Calories',
            data: caloriesData,
            fill: true,
            backgroundColor: 'rgba(255, 99, 132, 0.2)',
            borderColor: 'rgb(255, 99, 132)',
            pointBackgroundColor: 'rgb(255, 99, 132)',
            pointBorderColor: '#fff',
            pointHoverBackgroundColor: '#fff',
            pointHoverBorderColor: 'rgb(255, 99, 132)'
          }
        ]
      });
    }
    }, [selectedDate, nutritionItems]); 

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const
      },
      title: {
        display: true,
        text: 'Calories Visualization'
      }
    },
    scales: {
      r: {
        min: 0,  
        ticks: {
          stepSize: 100, 
        },
      },
    },

  };

  return (
    <MainLayout>
      <div>
        <h1>Dashboard Page</h1>
        <p>Welcome to the dashboard.</p>
        <div className='date-div'>
          <label className='label'>日付：</label>
          <input id='date' className='select-kind' type="date" value={selectedDate} onChange={(e) => setSelectedDate(e.target.value)}/>
        </div>
        <div className='radar-chart'>
          <Radar data={data} options={options} />
        </div>
      </div>
    </MainLayout>
  );
};

export default Dashboard;
